func:
// initialise the counter to 0 if it doesn't exist already
var count = context.get('count')||0;
count += 1;
// store the value back
context.set('count',count);
// make it part of the outgoing msg object
msg.payload.d.count = count;
if(count % 100 <= 90) {
 msg.payload.d.voltage = Math.floor(Math.random() * (240 - 220) + 220);
} else {
 msg.payload.d.voltage = Math.floor(Math.random() * (260 - 240) + 240);
}
msg.payload.d.frequency = Math.floor(Math.random() * (80 - 60 + 1) + 60);
//msg.payload = JSON.stringify(msg.payload)
msg.eventOrCommandType = "voltage"
global.set('voltages',msg.payload.d.voltage);
return msg;

//initialize a list that stores the last n values for the electric current by using the voltage parameter. In stream computing, this list is called a "sliding window of fixed size." 
var aggwindow = context.get('aggwindow')||[];

//add values to that lis
aggwindow.push(msg.payload.d.voltage); 

//Continue adding values to the list until we have exceeded 30 values, which defines the size of our sliding window. 
if (aggwindow.length> 30) {

 //To compute z-score, we need the mean and the standard deviation.
 sum = aggwindow.reduce((a,b)=>a+b,0);
 n = aggwindow.length;
 mean = sum/n;
 sd = Math.sqrt(aggwindow.map(x=>Math.pow(mean-x,2)).reduce((a,b)=>a+b,0));

 // get rid of the oldest element in the list, which resembles a LIFO. 
 aggwindow.shift();

 //Perform Z-Score Calculation. Add a small value to standard deviation because the standard deviation can become zero, which is mathematically undefined
 msg.zscore = (mean-msg.payload.d.voltage)/(sd+0.0001)
}

//store this list to a global context to preserve it over individual message lifetimes
context.set('aggwindow',aggwindow);


//output voltage and zscore
msg.payload = {
 [("voltage")]:global.get("voltages"),
 [("zscore")]:Math.abs(msg.zscore)
 }

return msg;
